import { type } from '@testing-library/user-event/dist/type';
import React, {useEffect, useSate} from 'react'

export default function Filter({setActive, activeButton, setFiltered, data}) {

    useEffect(()=>{
        if(activeButton === 'all'){
          setFiltered(data);
          return;
        } 
        const filtered = data.filter((item)=>
          item.type.includes(type)
        );
        setFiltered(filtered);
      },[activeButton]);

  return (
    <div className="filter-container">
        <div className="list" onClick={()=> setActive('all')}>All</div>
        <div className="list" onClick={()=> setActive('UI/UX')}>UI/UX</div>
        <div className="list" onClick={()=> setActive('website')}>Website</div>
        <div className="list" onClick={()=> setActive('Coding')}>Coding</div>
    </div>
  )
}
